function check() {
  const book = document.getElementById("des");
  book.addEventListener("load", (e) => {
    e.preventDefault();
    const bookId = book.getAttribute("des");
    const XHR = new XMLHttpRequest();
    XHR.open("DELETE", `/books/${bookId}`);
    XHR.responseType = "json";
    XHR.send();
    XHR.onload = () => {
      const item =  XHR.response.book;
      const dii = document.getElementById("@book.id")
      if (item.created) {
        book.destroy();
      } else if (item.destroyed) {
        dii.destroy();
      }
    };
  });
}

window.addEventListener("load", check);
